package org.example.ui.models;import com.sedmelluq.discord.lavaplayer.track.AudioTrack;import net.dv8tion.jda.api.entities.Guild;import org.example.Stage;import org.example.lavaplayer.PlayerManager;import org.example.lavaplayer.TrackScheduler;import org.example.utils.Utils;import javax.security.auth.callback.Callback;public class StageInfo {    private static final String AUDIO_RESOURCE_PATH = "src/main/resources/audio/";    Stage stage;    String audioPath;    int delay;    public StageInfo(int delay, String audioPath, Stage stage) {        this.stage = stage;        this.audioPath = audioPath;        this.delay = delay;    }    public void play(Guild guild, EndTrackCallback callback) {        PlayerManager.get().play(guild, AUDIO_RESOURCE_PATH + audioPath, (track) -> {            Utils.startTimer(delay, callback::onTrackEnd);        });    }    @FunctionalInterface    public interface EndTrackCallback {        void onTrackEnd();    }}